INSERT INTO `document` VALUES ('8', 'ionic轮播图不循环轮播', '<p>\r\n	<span style=\"color:#333333;font-family:\'Helvetica Neue\', \'Luxi Sans\', \'DejaVu Sans\', Tahoma, \'Hiragino Sans GB\', STHeiti;font-size:15px;line-height:25.5px;background-color:#FFFFFF;\">ng-repeat的数据是延迟加载过来的。当img没有渲染完是不能工作的。像下面这样修改一下就可以了</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-family:\'Helvetica Neue\', \'Luxi Sans\', \'DejaVu Sans\', Tahoma, \'Hiragino Sans GB\', STHeiti;font-size:15px;line-height:25.5px;background-color:#FFFFFF;\"><ion-slide-box <span=\"\" style=\"color: rgb(229, 51, 51);\">ng-if=\"banners\"</span>&nbsp;on-slide-changed=\"slideHasChanged($index)\" auto-play=\"true\" does-continue=\"true\" slide-interval=2000 show-pager=\"true\" pager-click=\"pageClick(index)\" active-slide=\"model.activeIndex\" delegate-handle=\"delegateHandler\"&gt;\r\n</p>', '', '8', '30', '1467716002', '1467716002', '1', 'ionic,轮播图,不循环轮播', 'ionic,轮播图,不循环轮播');
